// page init
jQuery(function(){
	initAnchors();
	initTouchNav();
	initOpenClose();
	initMobileNav();
	initSameHeight();
	initCustomHover();
	initScrollOut();
	jQuery('input, textarea').placeholder();
});

function initScrollOut() {
	var win = jQuery(window);
	var headerClass = 'slideOutUp';
	var scrollHeight = 1;
	var header = jQuery('#wrapper');

	// add class to header
	function addClass(){
		var scrollTop = win.scrollTop();
		if (scrollTop > scrollHeight){
			header.addClass(headerClass);
		} else {
			header.removeClass(headerClass);
		}
	}

	win.on('scroll', addClass);
}

// open-close init
function initOpenClose() {
	jQuery('.open-close-1').openClose({
		activeClass: 'active',
		opener: '.opener-1',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
	jQuery('.open-close-1').openClose({
		activeClass: 'active',
		opener: '.opener-2',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('.wrap2').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'active',
		menuOpener: '.opener',
		menuDrop: '.drop'
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: '.back-to-top',
		extraOffset: function() {
			var totalHeight = 0;
			jQuery('0').each(function(){
				totalHeight += jQuery(this).outerHeight();
			});
			return totalHeight;
		}
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.sameheight-block').sameHeight({
		elements: '.sameheight',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('.nav').each(function(){
		new TouchNav({
			navBlock: this,
			menuDrop: '.drop-nav'
		});
	});

	jQuery('.drop-wrap').each(function(){
		new TouchNav({
			navBlock: this,
			menuDrop: 'ul'
		});
	});



	// detect device type
	var isTouchDevice = ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch,
		isWinPhoneDevice = /Windows Phone/.test(navigator.userAgent);

	// define events
	var eventOn = (isTouchDevice && 'touchstart') || (isWinPhoneDevice && navigator.pointerEnabled && 'pointerdown') || (isWinPhoneDevice && navigator.msPointerEnabled && 'MSPointerDown') || 'mouseenter',
		eventOff = (isTouchDevice && 'touchend') || (isWinPhoneDevice && navigator.pointerEnabled && 'pointerup') || (isWinPhoneDevice && navigator.msPointerEnabled && 'MSPointerUp') || 'mouseleave';

	var win = jQuery(window),
		header = jQuery('#header');

	jQuery('#main-nav ul').each(function() {
		var nav = jQuery(this),
			items = nav.find('>li');

		items.each(function() {
			var item = jQuery(this),
				itemDrop = item.find('.drop-wrap'),
				subItems = item.find('.drop-wrap .col > ul > li'),
				hoverClass = 'sub-hover';


			if (itemDrop.length) {
				item.on(eventOn, function() {
					if (win.height() < header.innerHeight() + itemDrop.innerHeight()) {
						itemDrop.css({
							height: win.height() - header.innerHeight()
						});
						item.addClass(hoverClass);
					} else {
						subItems.each(function() {
							var subItem = jQuery(this),
								subDrop = subItem.find('.drop-wrap-1');

							if (subDrop.length) {
								subItem.on(eventOn, function() {
									if (header.innerHeight()  + itemDrop.innerHeight() < subDrop.offset().top + subDrop.innerHeight()) {
										itemDrop.css({
											height: itemDrop.innerHeight() + ((subDrop.offset().top + subDrop.innerHeight()) - (itemDrop.offset().top + itemDrop.innerHeight()))
										});
									}
								});

								subItem.on(eventOff, function() {
									itemDrop.css({
										height: ''
									});
								});
							}
						});
					}
				});

				item.on(eventOff, function() {
					itemDrop.css({
						height: ''
					});
					item.removeClass(hoverClass);
				});
			}
		});
	});
}

// add classes on hover/touch
function initCustomHover() {
	jQuery('.row .col, .block-mission .block').touchHover();
	jQuery('.block-mission').touchHover({
		hoverClass: 'block-active'
	});
}

